const e=JSON.parse('{"key":"v-3fab32e4","path":"/travel/2024.10.3%20%E9%80%86%E6%9C%9D%E4%BA%94%E5%8F%B0%E5%B1%B1.html","title":"","lang":"zh-CN","frontmatter":{},"headers":[{"level":2,"title":"10.2","slug":"_10-2","link":"#_10-2","children":[]},{"level":2,"title":"10.3","slug":"_10-3","link":"#_10-3","children":[]},{"level":2,"title":"10.4","slug":"_10-4","link":"#_10-4","children":[]},{"level":2,"title":"10.5","slug":"_10-5","link":"#_10-5","children":[]},{"level":2,"title":"10.6","slug":"_10-6","link":"#_10-6","children":[]},{"level":2,"title":"开支","slug":"开支","link":"#开支","children":[]},{"level":2,"title":"附录：准备","slug":"附录-准备","link":"#附录-准备","children":[]}],"git":{"createdTime":1729182997000,"updatedTime":1729182997000,"contributors":[{"name":"fenghao","email":"fenghao9523@gmail.com","commits":1}]},"readingTime":{"minutes":9.15,"words":2744},"filePathRelative":"travel/2024.10.3 逆朝五台山.md","localizedDate":"2024年10月17日","excerpt":"<h2> 10.2</h2>\\n<p><strong>杭州 -&gt; 太原：飞机</strong></p>\\n<p>起因是98上刷到了有同学发的贴征集同行，看了一眼行程挺有意思，而且国庆居然才涨价这么点（898 -&gt; 950），考虑了一段时间后就报了团。</p>\\n<p>第一次用了支付宝的权益，搞了快速安检和电瓶车，好评。在不用托运的情况下，从地铁到站到坐等候机差不多只花了20min，下次可以晚一点出门了。</p>\\n<p>在机场等待时间里无聊一直在刷xhs上五台山相关的贴，看到说是10.1的天气非常恶劣，大风、大雾、低温（白天都能接近零度），搞得十分紧张，就怕环境过于恶劣。但最后运气还挺好，去徒步的三天都是晴天，气温也比较适宜，白天基本都在20度左右。</p>"}');export{e as data};
